    def changeUser(self):
        text, ok = QtWidgets.QInputDialog.getText(self, 'xác thực', 'Nhập lại mật khẩu')
        if ok and text == '2122000':
            key = b'[EX\xc8\xd5\xbfI{\xa2$\x05(\xd5\x18\xbf\xc0\x85)\x10nc\x94\x02)j\xdf\xcb\xc4\x94\x9d(\x9e'
