    def encrypt_file(self):
        key = b'[EX\xc8\xd5\xbfI{\xa2$\x05(\xd5\x18\xbf\xc0\x85)\x10nc\x94\x02)j\xdf\xcb\xc4\x94\x9d(\x9e'
        enc = Encryptor(key)
        filepath = self.lineEdit.text()
        try:
            enc.encrypt_file(filepath)
            msgBox= QtWidgets.QMessageBox()
            msgBox.setIcon(QtWidgets.QMessageBox.Information)
            msgBox.setText("mã hóa thành công!")
            msgBox.exec()
        except:
            msgBox= QtWidgets.QMessageBox()
            msgBox.setIcon(QtWidgets.QMessageBox.Critical)
            msgBox.setText("lỗi file!")
            msgBox.exec()
    def decrypt_file(self):
        filepath = self.lineEdit.text()
        key = b'[EX\xc8\xd5\xbfI{\xa2$\x05(\xd5\x18\xbf\xc0\x85)\x10nc\x94\x02)j\xdf\xcb\xc4\x94\x9d(\x9e'
        enc = Encryptor(key)
        enc.decrypt_file("./pickle/label.pickle.enc")
        enc.decrypt_file("./pickle/classifier.pickle.enc")
        recog = Recognizer()
        detection = recog.recognize()
        enc.encrypt_file("./pickle/label.pickle")
        enc.encrypt_file("./pickle/classifier.pickle")
        try:
            if(detection):
                enc.decrypt_file(filepath)
                msgBox= QtWidgets.QMessageBox()
                msgBox.setIcon(QtWidgets.QMessageBox.Information)
                msgBox.setText("giải mã thành công!")
                msgBox.exec()
            else:
                msgBox= QtWidgets.QMessageBox()
                msgBox.setIcon(QtWidgets.QMessageBox.Critical)
                msgBox.setText("người dùng không tin cậy!")
                msgBox.exec()
        except:
            msgBox= QtWidgets.QMessageBox()
            msgBox.setIcon(QtWidgets.QMessageBox.Critical)
            msgBox.setText("lỗi file!")
            msgBox.exec()

